const std = @import("std");

pub const Coding = enum {
    AntiCollision,
    Manchester
};

pub fn Decoder(
    SamplesPerBit: usize,
    coding: Coding,
) type {
    if (SamplesPerBit % 4 != 0) {
        @panic("SamplesPerBit must be divisible by 4");
    }

    return struct {
        const Self = @This();

        const INVALID_SAMPLES: usize = 2;
        const SAMPLES_PER_BIT: usize = SamplesPerBit;
        const HALF: usize = SAMPLES_PER_BIT / 2;
        const QUARTER: usize = SAMPLES_PER_BIT / 4;
        const DOM_HALF: usize = HALF - INVALID_SAMPLES;
        const DOM: usize = QUARTER - INVALID_SAMPLES;

        pub fn decodeStruct(
            self: *Self,
            comptime T: type,
            samples: []const u1,
            header: []const u1,
        ) !T {
            const bits = @bitSizeOf(T);
            const IntT = std.meta.Int(.unsigned, bits);
            comptime {
                if (bits > 64) {
                    @compileError("bits too big?");
                }
            }

            var buf: [bits]u1 = @splat(0);
            _ = try self.decode(samples, header, buf[0..]);

            var value: IntT = 0;
            for (buf, 0..) |bit, i| {
                // TODO: bug? when bits is too big?
                value |= @as(IntT, bit) << @truncate(bits - 1 - i);
            }
            return @bitCast(value);
        }

        pub fn decode(
            self: *Self,
            samples: []const u1,
            header: []const u1,
            out_bits: []u1,
        ) !usize {
            _ = self;

            if (header.len == 0 or out_bits.len == 0)
                return error.InvalidInputs;

            var offset: usize = 0;
            while (offset < samples.len) : (offset += 1) {
                if (tryDecodeAtOffset(samples, offset, header, out_bits)) |n| {
                    return n;
                }
            }
            return error.CantFind;
        }

        fn tryDecodeAtOffset(
            samples: []const u1,
            offset: usize,
            sof: []const u1,
            out_buf: []u1,
        ) ?usize {
            var bit_idx: usize = 0;
            var pos: usize = offset;

            while (true) {
                const bit = decodeBit(samples, pos) orelse {
                    return null;
                };
                out_buf[bit_idx] = bit;
                bit_idx += 1;

                // check HEADER
                if (bit_idx == sof.len) {
                    if (!std.mem.eql(u1, out_buf[0..sof.len], sof)) {
                        return null;
                    }
                }
                if (bit_idx >= out_buf.len) {
                    break;
                }
                pos += SAMPLES_PER_BIT;
                if (pos > samples.len) {
                    break;
                }
            }

            if (bit_idx != out_buf.len) {
                return null;
            }
            return bit_idx;
        }

        fn decodeBit(samples: []const u1, start: usize) ?u1 {
            return switch (coding) {
                .AntiCollision => decodeAcBit(samples, start),
                .Manchester => decodeManchesterBit(samples, start),
            };
        }

        fn quarterState(q: []const u1) ?bool {
            var high: usize = 0;
            for (q) |v| {
                if (v == 1) high += 1;
            }
            const low = q.len - high;

            if (high >= DOM) return true;
            if (low >= DOM) return false;
            return null;
        }

        fn decodeAcBit(samples: []const u1, start: usize) ?u1 {
            if (start + SAMPLES_PER_BIT > samples.len)
                return null;

            const q0 = samples[start + 0 * QUARTER .. start + 1 * QUARTER];
            const q1 = samples[start + 1 * QUARTER .. start + 2 * QUARTER];
            const q2 = samples[start + 2 * QUARTER .. start + 3 * QUARTER];
            const q3 = samples[start + 3 * QUARTER .. start + 4 * QUARTER];

            const s0 = quarterState(q0);
            const s1 = quarterState(q1);
            const s2 = quarterState(q2);
            const s3 = quarterState(q3);

            if (s0 == null or s1 == null or s2 == null or s3 == null)
                return null;

            // AC patterns
            if (s0.? and s1.? and !s2.? and !s3.?)
                return 0;

            if (s0.? and !s1.? and s2.? and !s3.?)
                return 1;

            return null;
        }

        fn halfState(q: []const u1) ?bool {
            var high: usize = 0;
            for (q) |v| {
                if (v == 1) high += 1;
            }
            const low = q.len - high;

            if (high >= DOM_HALF) return true;
            if (low >= DOM_HALF) return false;
            return null;
        }

        fn decodeManchesterBit(samples: []const u1, start: usize) ?u1 {
            if (start + SAMPLES_PER_BIT > samples.len)
                return null;

            const h0 = samples[start .. start + HALF];
            const h1 = samples[start + HALF .. start + SAMPLES_PER_BIT];

            const s0 = halfState(h0);
            const s1 = halfState(h1);

            if (s0 == null or s1 == null)
                return null;

            // Manchester:
            // 0 = H -> L
            if (!s0.? and s1.?)
                return 0;

            // 1 = L -> H
            if (s0.? and !s1.?)
                return 1;

            return null;
        }
    };
}

